import '../index.css';
import DownloadButton from '../components/Button';
import Footer from '../components/Footer';
import ProfileCard from '../components/Card/ProfileCard';
import {CircularProgress, Card, CardHeader, CardBody, CardFooter, Image, Button, Chip} from "@nextui-org/react";

const skills =[
  {
    name: "Python",
    percentage: 60,
  } ,
  {
    name: "React JS",
    percentage: 30,
  } ,
  {
    name: "HTML",
    percentage: 70,
  } ,
  {
    name: "Java",
    percentage: 40,
  } ,
]

export default function About() {
  return (
    <div className='min-h-screen'>
      <ProfileCard />

      <MyBackground />

      <div className='text-center mt-3'>
        <span className='text-3xl font-semibold text-gray-700'>Skills</span>
        <div className="w-full mt-7 gap-4 grid grid-cols-1 md:flex-wrap md:flex justify-center items-center">

          <SkillCard path="assets/Images/CardImages/computer vision.jpeg" number="01" title="Computer Vision" />
          <SkillCard path="assets/Images/CardImages/dataScience.jpeg" number="02" title="Machine Learning" />
          <SkillCard path="assets/Images/CardImages/web design.jpeg" number="03" title="Web development" />

      </div>
      </div>


      <Footer title={"Follow me"} lineWidth={'3/4'} />
    </div>
  );
}

function SkillCard({path, number, title}) {
  return (
  <Card className="h-[250px] sm:h-[250px] md:h-[300px] lg:h-[350px] xl:h-[350px] w-[180px] sm:w-[180px] md:w-[220px] lg:w-[250px] xl:w-[300px]] mx-auto md:mx-0">
    <Image
      removeWrapper
      alt="Card background"
      className="z-0 w-full h-full object-cover brightness-50"
      src={path} />
    <CardFooter className="absolute z-10 bottom-0 flex-col !items-start">
      <p className="text-tiny text-white/60 uppercase font-bold">{number}</p>
      <h4 className="text-white font-medium text-large">{title}</h4>
    </CardFooter>
  </Card>
  )}

// This could be a component in the container folder.
function MyBackground() {
  return (
    <div className="mt-9 mx-5 w-full flex ">
      <div className='w-1/2'>
        <span className="text-3xl font-semibold text-gray-700">My Background</span>
        <p className="mt-5 text-gray-600 text-[18px]">
          A graduate student from the University of Nottingham, pursed a bachelor in Computer Science with Artificial intelligence.
          During my academic journey, I had the chance to experience different areas of computer science, and found myself more into 
          web developing, machine learning, and data analysis.
        </p>
        <DownloadButton path="assets/PDFs/Rashed Alaleeli Resume.pdf" title="Download CV"/>
      </div>
      <div className='w-1/2 flex justify-center'>
        <img src='assets/Images/student.jpeg' alt='student' className='h-full drop-shadow-xl'/>
      </div>
    </div>
  )
}
