import { Modal, ModalContent, ModalHeader, ModalBody, ModalFooter, Button, ScrollShadow, useDisclosure } from "@nextui-org/react";
import { motion, useScroll } from "framer-motion";
import { useState, useEffect } from "react";

export default function ResearchModal({ modalTitle, modalBody }) {
    const { isOpen, onOpen, onOpenChange } = useDisclosure();
    // const ref = useRef(null)
    // const { scrollYProgress } = useScroll({container: ref});

    const [scrollYProgress, setScrollYProgress] = useState(1);

    useEffect(() => {
        const handleScroll = () => {
            const scrollableElement = document.querySelector('.modalBody'); // Replace with the correct selector for your scrollable element
            if (scrollableElement) {
                const scrollTop = scrollableElement.scrollTop;
                const scrollHeight = scrollableElement.scrollHeight;
                const clientHeight = scrollableElement.clientHeight;
                const progress = scrollTop / (scrollHeight - clientHeight);
                setScrollYProgress(progress);
            }
        };

        if (isOpen) {
            window.addEventListener('scroll', handleScroll);
        }

        return () => {
            window.removeEventListener('scroll', handleScroll);
        };
    }, [isOpen]);

    return (
        <div className="items-center flex justify-center" >
            <Button onPress={onOpen} className="bg-gray-true-300" variant="shadow">Read More</Button>
            <Modal
                size="4xl"
                isOpen={isOpen}
                onOpenChange={onOpenChange}
                scrollBehavior="inside"
                backdrop="blur"
                className="modalBody"

            >
                <ModalContent>
                    {(onClose) => (
                        <>
                            <ModalHeader className="flex flex-col gap-1 group"> 
                                {modalTitle}
                                <motion.div
                                    className="h-2 bg-blue-500 absolute left-0 bottom-0"
                                    style={{
                                        width: `${scrollYProgress * 100}%`,
                                    }}
                                ></motion.div>
                            </ModalHeader>
                            <ModalBody>
                                <ScrollShadow>
                                    {modalBody }
                                </ScrollShadow>
                            </ModalBody>

                            <ModalFooter>
                                <Button className="bg-gray-true-300" onPress={onClose}>
                                    Close
                                </Button>
                            </ModalFooter>
                        </>
                    )}
                </ModalContent>
            </Modal>
        </div>

    );
}


