import { useState, useEffect } from 'react';
import Logo from '../Logo';
import MenuButton from './MenuButton';
import NavLink from './NavLink';
import { motion } from 'framer-motion';

const variants = {
  open: {
    transition: { staggerChildren: 0.07, delayChildren: 0.2 },
  },
  closed: {
    transition: { staggerChildren: 0.05, staggerDirection: -1 },
  }
};

export default function NavigationBar() {
  const [isOpen, setIsOpen] = useState(false);
  const [isSticky, setSticky] = useState(false)

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 0) {
        setSticky(true);
      } else {
        setSticky(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  function toggleMenu() {
    setIsOpen(!isOpen);
  };

  return (
    <motion.nav 
      animate={isOpen ? "open" : "closed"}
    >
      <div className="max-w-screen-xl flex flex-wrap items-center bg-gray-true-800 justify-between mx-auto p-4 relative">
        <Logo />
        <MenuButton onToggleMenu={toggleMenu} />

        <div className={`w-full md:block md:w-auto ${!isOpen ? 'hidden' : ''}`}>
          <motion.ul className="font-medium flex flex-col pt-2 md:p-0 rounded-lg md:space-x-8 md:flex-row md:border-0 bg-gray-true-800 " variants={variants}>
            <NavLink to="/" onToggleMenu={toggleMenu}>Home</NavLink>
            <NavLink to="/Projects" onToggleMenu={toggleMenu}>Projects</NavLink>
            <NavLink to="/About" onToggleMenu={toggleMenu}>About</NavLink>
            <NavLink to="/Contact" onToggleMenu={toggleMenu}>Contact</NavLink>
          </motion.ul>
        </div> 

      </div>
    </motion.nav>
  );
}
