import '../index.css';
import Footer from '../components/Footer'
import ContactForm from '../components/Form/ContactForm';
import HeroSection from '../components/HeroSection';

export default function Contact() {
  return (
    <>
    <HeroSection path="assets/images/IMG_2847.jpg" title="CONTACT US" />

    <section class="relative z-10 overflow-hidden bg-white py-20 lg:py-[120px]">
      <div class="container mx-auto">
        <div class="-mx-4 flex flex-wrap lg:justify-between">
          <div class="w-full px-4 lg:w-1/2 xl:w-6/12 ">
            <div class="mb-12 max-w-[570px] lg:mb-0 ml-5 mr-4">

              <h2 class="mb-6 text-[32px] font-bold uppercase sm:text-[40px] lg:text-[36px] xl:text-[40px]">
                Contact Information
              </h2>
              <p class="mb-9 text-base leading-relaxed">
                If you have any questions or inquiries, feel free to reach out to us. We are here to assist you with any information you may need.
              </p>

              <ContactInfo img="assets/icons/telephone.png" type="Phone Number" info="(+971) 50 851 2124" />
              <ContactInfo img="assets/icons/mail.png" type="Email Address" info="ralaleeli@gmail.com" />

            </div>
          </div>

          <ContactForm />

        </div>
      </div>
    </section>

    <Footer title={"Follow me"} lineWidth={'3/4'}/>
    </>

  );
}

const Mail = ({size=24, color="#000000"}) => (<svg xmlns="http://www.w3.org/2000/svg" width={size} height={size} viewBox="0 0 24 24" fill="none" stroke={color} strokeWidth="1.5" strokeLinecap="square" strokeLinejoin="round"><path d="M4 4h16c1.1 0 2 .9 2 2v12c0 1.1-.9 2-2 2H4c-1.1 0-2-.9-2-2V6c0-1.1.9-2 2-2z"></path><polyline points="22,6 12,13 2,6"></polyline></svg>);

const Phone = ({size=24, color="#000000"}) => (<svg xmlns="http://www.w3.org/2000/svg" width={size} height={size} viewBox="0 0 24 24" fill="none" stroke={color} strokeWidth="1.5" strokeLinecap="square" strokeLinejoin="round"><path d="M22 16.92v3a2 2 0 0 1-2.18 2 19.79 19.79 0 0 1-8.63-3.07 19.5 19.5 0 0 1-6-6 19.79 19.79 0 0 1-3.07-8.67A2 2 0 0 1 4.11 2h3a2 2 0 0 1 2 1.72 12.84 12.84 0 0 0 .7 2.81 2 2 0 0 1-.45 2.11L8.09 9.91a16 16 0 0 0 6 6l1.27-1.27a2 2 0 0 1 2.11-.45 12.84 12.84 0 0 0 2.81.7A2 2 0 0 1 22 16.92z"></path></svg>);


function ContactInfo({img, type, info}) {
  return (
    <div class="mb-8 flex w-full max-w-[370px]">
      <div class="bg-primary text-primary mr-6 flex h-[60px] w-full max-w-[60px] items-center justify-center overflow-hidden rounded bg-opacity-0 sm:h-[70px] sm:max-w-[70px]">
        {/* <img src={img} alt="mail" class="h-10 mb-2" /> */}
      </div>
      <div class="w-full">
        <h4 class="text-dark mb-1 text-xl font-bold">{type}</h4>
        <a href="mailto:ralaleeli@gmail.com" class="text-base">{info}</a>
      </div>
    </div>
  );
}
