import '../index.css';
import DownloadButton from '../components/Button';
import Footer from '../components/Footer';
import ProfileCard from '../components/Card/ProfileCard';
import {CircularProgress, Card, CardHeader, CardFooter, Image, Button, Chip} from "@nextui-org/react";

const skills =[
  {
    name: "Python",
    percentage: 60,
  } ,
  {
    name: "React JS",
    percentage: 30,
  } ,
  {
    name: "HTML",
    percentage: 70,
  } ,
  {
    name: "Java",
    percentage: 40,
  } ,
];

export default function About() {
  return (
    <div className='min-h-screen'>
      <ProfileCard />

      <MyBackground />
      <DownloadButton path="assets/PDFs/Rashed Alaleeli Resume.pdf" title="Download CV"/> 

      <div className='text-center mt-3'>
        <span className='text-3xl font-semibold text-gray-700'>Skills</span>
        <div className="flex justify-center items-center mt-3 space-x-8">
          <Card className="h-[250px] sm:h-[250px] md:h-[300px] lg:h-[350px] xl:h-[400px] w-[180px] sm:w-[180px] md:w-[220px] lg:w-[250px] xl:w-[300px]] mx-auto md:mx-0">
              <CardHeader className="absolute z-10 top-1 flex-col !items-start">

              </CardHeader>
              <Image
                  removeWrapper
                  alt="Card background"
                  className="z-0 w-full h-full object-cover brightness-50"
                  src={image}
              />
              <CardFooter className="justify-between before:bg-white/10 border-white/20 border-1 overflow-hidden py-1 absolute before:rounded-xl rounded-large bottom-1 w-[calc(100%_-_8px)] shadow-small ml-1 z-10">
                  <p className="text-tiny text-white/60 uppercase font-bold">{subtitle}</p>
                  <h4 className="text-white font-medium text-large">{title}</h4>
              </CardFooter> 
          </Card>

        {/* { skills.map((skill) => (
          <Card className="w-[240px] h-[240px] border-none">
          <CardBody className="justify-center items-center pb-0">
            <CircularProgress
              classNames={{
                svg: "w-36 h-36 drop-shadow-md",
                indicator: "stroke-gray-700",
                track: "stroke-gray-700/10",
                value: "text-3xl font-semibold text-gray-700",
              }}
              value={skill.percentage}
              strokeWidth={4}
              showValueLabel={true}
            />
          </CardBody>
          <CardFooter className="justify-center items-center pt-0">
            <Chip
              classNames={{
                base: "border-1 border-gray/30",
                content: "text-gray/90 text-small font-semibold",
              }}
              variant="bordered"
            >
              {skill.name}
            </Chip>
          </CardFooter>
        </Card>
        ))} */}

      </div>
      </div>


      <Footer title={"Follow me"} lineWidth={'3/4'} />
    </div>
  );
}

// This could be a component in the container folder.
function MyBackground() {
  return (
    <div className="text-center mt-9">
      <span className="text-3xl font-semibold text-gray-700">My Background</span>
      <p className="mt-5 text-gray-600 text-[18px]">
        A graduate student from the University of Nottingham, pursed a bachelor in Computer Science with Artificial intelligence.
        During my academic journey, I had the chance to experience different areas of computer science, and found myself more into 
        web developing, machine learning, and data analysis.
      </p>
    </div>
  )
}
