import { ResearchModal } from "./ResearchModal";

export default function RightColumn({ title, body, children }) {
    return (
        <div class="w-full md:w-2/3 text-left px-4 font-custom">
            <p class="text-2xl font-semibold"> About </p>
            <p class=" mb-5 text-lg"> {children} </p>

            { (title && body) ? <ResearchModal body={body} title={title} /> : '' }
        </div>
    );
}
