import { Modal, ModalContent, ModalHeader, ModalBody, ModalFooter, Button, ScrollShadow, useDisclosure } from "@nextui-org/react";

export function ResearchModal({ title, body }) {
    const { isOpen, onOpen, onOpenChange } = useDisclosure();

    return (
        <div className="items-center flex justify-center">
            {/* <Button onPress={onOpen} color="primary" variant="shadow">Read More</Button> */}
            <a>Read more</a>
            <Modal
                size="4xl"
                isOpen={isOpen}
                onOpenChange={onOpenChange}
                scrollBehavior="inside"
                backdrop="blur"
            >
                <ModalContent>
                    {(onClose) => (
                        <>
                            <ModalHeader className="flex flex-col gap-1">{title}</ModalHeader>
                            <ModalBody>
                                <ScrollShadow>
                                    {body}
                                </ScrollShadow>
                            </ModalBody>
                            <ModalFooter>
                                <Button color="primary" onPress={onClose}>
                                    Close
                                </Button>
                            </ModalFooter>
                        </>
                    )}
                </ModalContent>
            </Modal>
        </div>

    );
}
