import '../index.css';
import Footer from '../components/Footer';
import ProfileCard from '../components/Card/ProfileCard';
import MyBackground from '../container/About/';
import NextCard from '../components/Card/NextCard';
import { motion } from "framer-motion";

const skills =[
  {
    name: "Computer Vision",
    imagePath: "assets/Images/CardImages/computer vision.jpeg",
  } ,
  {
    name: "Machine Learning",
    imagePath: "assets/Images/CardImages/dataScience.jpeg",
  } ,
  {
    name: "Web Development",
    imagePath: "assets/Images/CardImages/web design.jpeg",
  } ,
]

export default function About() {
  return (
    <motion.div 
      initial={{ opacity: 0.3 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      transition={{ duration: 0.5 }}
      className='min-h-screen'
    >
      <ProfileCard />

      <MyBackground />

      <div className='text-center mt-3'>
        <span className='text-4xl font-semibold text-gray-700'>Skills</span>
        <p className='mt-1 text-gray-600 text-[18px]'>These are some of the skills I developed myself in during university.</p>
        <div className="w-full mt-5 gap-4 flex-wrap flex justify-center items-center">

        {skills.map((skill) => <NextCard image={skill.imagePath} number={`${(skills.indexOf(skill) + 1 < 10 ? '0' : '')}${skills.indexOf(skill) + 1}`} title={skill.name}/>)}

      </div>
      </div>

    </motion.div>
  );
}
