import { Modal, ModalContent, ModalHeader, ModalBody, ModalFooter, Button, ScrollShadow, useDisclosure } from "@nextui-org/react";
import { motion, useScroll } from "framer-motion";
import { useRef } from "react";
import { useEffect } from "react";

export default function ResearchModal({ modalTitle, modalBody }) {
    const { isOpen, onOpen, onOpenChange } = useDisclosure();
    const ref = useRef(null)
    const { scrollYProgress } = useScroll({container: ref});

    useEffect(() => {
        const handleScroll = () => {
            // Calculate the scroll position here and log it
            const scrollPosition = 100;
            console.log('Scroll position:', scrollPosition);
        };

        if (isOpen) {
            window.addEventListener('scroll', handleScroll);

            return () => {
                // Remove the scroll event listener when the modal is closed
                window.removeEventListener('scroll', handleScroll);
            };
        }
    }, [isOpen])

    return (
        <div className="items-center flex justify-center">
            <Button onPress={onOpen} className="bg-gray-true-300" variant="shadow">Read More</Button>
            <Modal
                size="4xl"
                isOpen={isOpen}
                onOpenChange={onOpenChange}
                scrollBehavior="inside"
                backdrop="blur"
            >
                <ModalContent>
                    {(onClose) => (
                        <>
                            <ModalHeader className="flex flex-col gap-1"> {modalTitle}
                                <motion.span 
                                    style={{ transformOrigin: 0, scaleX: scrollYProgress }}
                                    className="fixed bg-yellow-600 h-2 left-0 bottom-0 right-0 w-full z-50"
                                />

                            </ModalHeader>
                            <ModalBody >
                                <ScrollShadow>
                                    {modalBody}
                                </ScrollShadow>
                            </ModalBody>



                            <ModalFooter>
                                <Button className="bg-gray-true-300" onPress={onClose}>
                                    Close
                                </Button>
                            </ModalFooter>
                        </>
                    )}
                </ModalContent>
            </Modal>
        </div>

    );
}
