import { Modal, ModalContent, ModalHeader, ModalBody, ModalFooter, Button, ScrollShadow, useDisclosure } from "@nextui-org/react";
import { motion } from "framer-motion";

export default function ResearchModal({ modalTitle, modalBody }) {
    const { isOpen, onOpen, onOpenChange } = useDisclosure();

    return (
        <div className="items-center flex justify-center">
            <Button onPress={onOpen} className="bg-gray-true-300" variant="shadow">Read More</Button>
            <Modal
                size="4xl"
                isOpen={isOpen}
                onOpenChange={onOpenChange}
                scrollBehavior="inside"
                backdrop="blur"
            >
                <ModalContent>
                    {(onClose) => (
                        <>
                            <ModalHeader className="flex flex-col gap-1"> {modalTitle}
                            <svg className='flex w-12 h-12 mx-auto' viewBox='0 0 60 60'>
                                <motion.circle
                                fill='none'
                                strokeWidth='2'
                                stroke='black'
                                //d='M 0, 20 a 20, 20 0 1,0 40,0 a 20, 20 0 1,0 -40,0'
                                style={
                                    {

                                        rotate: 90,
                                        translateX: 5,
                                        translateY: 5,
                                        scaleX: -1, // Reverse direction of line animation
                                    }
                                }
                                />
                            </svg>
                            </ModalHeader>
                            <ModalBody>
                                <ScrollShadow>
                                    {modalBody}
                                </ScrollShadow>
                            </ModalBody>



                            <ModalFooter>
                                <Button className="bg-gray-true-300" onPress={onClose}>
                                    Close
                                </Button>
                            </ModalFooter>
                        </>
                    )}
                </ModalContent>
            </Modal>
        </div>

    );
}
