import '../index.css';
import Footer from '../components/Footer'
import ContactForm from '../components/Form/ContactForm';
import HeroSection from '../components/HeroSection';
import Mail from '../icons/Mail';
import Phone from '../icons/Phone';

export default function Contact() {
  return (
    <>
    <HeroSection path="assets/images/IMG_2847.jpg" title="CONTACT US" />

    <section class="relative z-10 overflow-hidden bg-white py-20 lg:py-[120px]">
      <div class="container mx-auto">
        <div class="-mx-4 flex flex-wrap lg:justify-between">
          <div class="w-full px-4 lg:w-1/2 xl:w-6/12 ">
            <div class="mb-12 max-w-[570px] lg:mb-0 ml-5 mr-4">

              <h2 class="mb-6 text-[32px] font-bold uppercase sm:text-[40px] lg:text-[36px] xl:text-[40px]">
                Contact Information
              </h2>
              <p class="mb-9 text-base leading-relaxed">
                If you have any questions or inquiries, feel free to reach out to us. We are here to assist you with any information you may need.
              </p>

              <div>
                <ContactInfo icon={Phone} title="Phone Number" info="(+971) 50 851 2124" />
                <ContactInfo icon={Mail} title="Email Address" info="ralaleeli@gmail.com" />
              </div>

            </div>
          </div>

          <ContactForm />

        </div>
      </div>
    </section>

    <Footer title={"Follow me"} lineWidth={'3/4'}/>
    </>

  );
}

function ContactInfo({icon, title, info}) {
  let href;

  if (title === "Phone Number") {
    href = "tel:+97150-851-2124";
  } else if (title === "Email Address") {
    href = "mailto:ralaleeli@gmail.com";
  } else {
    href = "#";
  }

  return (
    <div class="mb-8 flex w-full max-w-[370px] items-center">
      <div class="mr-6 flex h-[60px] w-full max-w-[60px] items-center justify-center overflow-hidden rounded bg-opacity-0 sm:h-[70px] sm:max-w-[70px]">
        {icon}
      </div>
      <div class="w-full">
        <h4 class="text-dark mb-1 text-xl font-bold">{title}</h4>
        <a href={href}>{info}</a>
      </div>
    </div>
  );
}
