import '../index.css';
import Footer from '../components/Footer';
import ProfileCard from '../components/Card/ProfileCard';
import {Card,  CardFooter, Image} from "@nextui-org/react";
import { MyBackground } from '../container/MyBackground';

const skills =[
  {
    name: "Computer Vision",
    imagePath: "assets/Images/CardImages/computer vision.jpeg",
  } ,
  {
    name: "Machine Learning",
    imagePath: "assets/Images/CardImages/dataScience.jpeg",
  } ,
  {
    name: "Web Development",
    imagePath: "assets/Images/CardImages/web design.jpeg",
  } ,
]

export default function About() {
  return (
    <div className='min-h-screen'>
      <ProfileCard />

      <MyBackground />

      <div className='text-center mt-3'>
        <span className='text-3xl font-semibold text-gray-700'>Skills</span>
        <div className="w-full mt-7 gap-4 grid grid-cols-1 md:flex-wrap md:flex justify-center items-center">

          {skills.map((skill) => <SkillCard path={skill.imagePath} number={skills.indexOf(skill)}/>)}

          {/* <SkillCard path="assets/Images/CardImages/computer vision.jpeg" number="01" title="Computer Vision" />
          <SkillCard path="assets/Images/CardImages/dataScience.jpeg" number="02" title="Machine Learning" />
          <SkillCard path="assets/Images/CardImages/web design.jpeg" number="03" title="Web development" /> */}

      </div>
      </div>


      <Footer title={"Follow me"} lineWidth={'3/4'} />
    </div>
  );
}

function SkillCard({path, number, title}) {
  return (
    <Card className="h-[250px] sm:h-[250px] md:h-[300px] lg:h-[350px] xl:h-[350px] w-[180px] sm:w-[180px] md:w-[220px] lg:w-[250px] xl:w-[300px]] mx-auto md:mx-0">
      <Image
        removeWrapper
        alt="Card background"
        className="z-0 w-full h-full object-cover brightness-50"
        src={path} />
      <CardFooter className="absolute z-10 bottom-0 flex-col !items-start">
        <p className="text-tiny text-white/60 uppercase font-bold">{number}</p>
        <h4 className="text-white font-medium text-large">{title}</h4>
      </CardFooter>
    </Card>
  )}
