import {Card, CardHeader, CardFooter, Image, Button} from "@nextui-org/react";
import { Link, useHistory } from 'react-router-dom';
import RightArrow from "../../icons/RightArrow";

export default function NextCard({title, subtitle, number, image, page}) {
    const histroy = useHistory();

    const handleClick = () => {
        // Handle the click event to navigate to the new page
        if (page) {
          history.push(page); // Navigate to the specified page
        }
      };

    return (
        <div className="w-1/2 sm:w-2/5 md:w-1/4 lg:w-1/5 mx-auto md:mx-0 cursor-pointer" onClick={<Link to={page} />}>
      <Card>
        {subtitle ? (
          <CardHeader className="absolute z-10 top-1 flex-col !items-start">
            <p className="text-tiny text-white/60 uppercase font-bold">{subtitle}</p>
            <h4 className="text-white font-medium text-large">{title}</h4>
          </CardHeader>
        ) : (
          ''
        )}
        <Image
          removeWrapper
          alt="Card background"
          className="z-0 w-full h-full object-cover brightness-50"
          src={image}
        />

        {page ? (
          <CardFooter className="justify-between before:bg-white/10 border-white/20 border-1 overflow-hidden py-1 absolute before:rounded-xl rounded-large bottom-1 w-[calc(100%_-_8px)] shadow-small ml-1 z-10">
            <p className="text-tiny text-white/80">Read More</p>
            <Button isIconOnly className="bg-transparent text-white">
              {RightArrow}
            </Button>
          </CardFooter>
        ) : (
          <CardFooter className="absolute z-10 bottom-0 flex-col !items-start">
            <p className="text-tiny text-white/60 uppercase font-bold">{number}</p>
            <h4 className="text-white font-medium text-large">{title}</h4>
          </CardFooter>
        )}
      </Card>
    </div>
  );
}
