import { useState } from 'react';
import Logo from '../Logo';
import MenuButton from './MenuButton';
import NavLink from './NavLink';
import { motion, AnimatePresence } from 'framer-motion';

const variants = {
  open: {
    transition: { staggerChildren: 0.07, delayChildren: 0.2 },
    // height: 0,
  },
  closed: {
    transition: { staggerChildren: 0.05, staggerDirection: -1 },
    // height: 'auto',
  }
};

const navContainer = {
  visible: {
    //x: 0,
    opacity: 1,
    transition: {
      x: { velocity: 100 },
      duration: 0.3
    }
  },
  hidden: {
    //x: -250,
    opacity: 0,
    transition: {
      x: { velocity: 100 },
      duration: 0.3
    }
  }
};

export default function NavigationBar() {
  const [isOpen, setIsOpen] = useState(false);

  function toggleMenu() {
    setIsOpen(!isOpen);
  };

  return (
    <motion.nav 
      animate={isOpen ? "open" : "closed"}
    >
    <nav className="bg-gray-true-800 border-gray-200">
      <div className="max-w-screen-xl flex flex-wrap items-center justify-between mx-auto p-4">
        <Logo />
        <MenuButton onToggleMenu={toggleMenu} />

        <AnimatePresence>
          { isOpen && (
          <motion.div className={`w-full md:block md:w-auto ${!isOpen ? 'hidden' : ''}`}>
            <motion.ul className="font-medium flex flex-col pt-2 md:p-0 rounded-lg md:flex-row  md:space-x-8 md:border-0 bg-gray-true-800 " variants={variants}>
              <NavLink to="/" onToggleMenu={toggleMenu}>Home</NavLink>
              <NavLink to="/Projects" onToggleMenu={toggleMenu}>Projects</NavLink>
              <NavLink to="/About" onToggleMenu={toggleMenu}>About</NavLink>
              <NavLink to="/Contact" onToggleMenu={toggleMenu}>Contact</NavLink>
            </motion.ul>
          </motion.div>)}
        </AnimatePresence>

      </div>
    </nav>
    </motion.nav>
  );
}
